const db = 'http://127.0.0.1:8000/api/'

export const saveLevel = async (level) => {

    const credentials = {
        username: localStorage.getItem('username'),
        levelAt: level,
    };
    console.log(credentials)

    try {
        const response = await fetch(db +'setlevel/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'X-CSRFToken': getCookie('csrftoken') 
            },
            body: JSON.stringify(credentials)
        });

        const data = await response.json();

        if (data.status === 'success') {
            alert('Your level has been updated.');

        } else {
            alert(data.message);
        }
    } catch (error) {
        console.error(error);
    }
}

export const getLevel = async () => {

    try {
        const response = await fetch('http://127.0.0.1:8000/api/login/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(credentials)
        });

        const data = await response.json();

        if (data.status === 'success') {

            localStorage.setItem('username', username);
            try {
                const response = await fetch('http://127.0.0.1:8000/api/level/', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ username: username })
                });

                const data = await response.json();

                if (data.status === 'success' && data.levelAt ) {
                    alert('Login successful!');

                    window.location.href = data.levelAt;

                } else {
                    alert("Something ain't right.");
                }
            } catch (error) {
                console.error(error);
            }

        } else if (data.status === 'error') {
            // Handle error case
            alert(data.message);
        }
    } catch (error) {
        console.error(error);
